ar naturalHeight = image.height;// 	    var bili = naturalWidth / naturalHeight;// 	    return bili;// 	}// 	var realBili = getNaturalWidth('/Public/ybt/image/login_bg@2x.png');// 	if(realBili > bili) {// 		//高取全屏// 		$('body').css('background-size','auto 100%');// 	} else {// 		//宽取全屏// 		$('body').css('background-size','100% auto');// 	}</script><script type="text/javascript">    // 发送手机短信    $lock = 0;    function send_sms_reg_code(){        var mobile = $('#username').val();        var tag = '';        if(checkMobile(mobile)){			var tag = 'mobile';        } else if(checkEmail(mobile)) {        	var tag = 'email';        } else {        	layer.msg('请输入正确的手机号码或邮箱');        	return;        }        if(tag == 'email') {        	var url = "/index.php/index/sem/sendemail/email/" + mobile;        } else if(tag == 'mobile') {        	var url = "/index.php/index/sem/send_sms_reg_code/mobile/" + mobile;        } else {        	return;        }        if($lock == 1) {            layer.msg('正在发送中，请稍后...');            return;        }        $lock = 1;		var type = 2;        $.get(url,{type:type},function(data){            obj = $.parseJSON(data);            if(obj.status == 1)            {                $('#count_down').attr("disabled","disabled");                intAs = 60; // 手机短信超时时间                jsInnerTimeout('count_down',intAs);            }            layer.msg(obj.msg);// alert(obj.msg);            $lock = 0;        })    }    $('#count_down').removeAttr("disabled");    //倒计时函数    function jsInnerTimeout(id,intAs)    {        var codeObj=$("#"+id);        intAs--;        if(intAs<=-1)        {            codeObj.removeAttr("disabled");            codeObj.text("获取验证码");            return true;        }        codeObj.text(intAs+'秒');        setTimeout("jsInnerTimeout('"+id+"',"+intAs+")",1000);    };    function checkMobile(tel) {        var reg = /(^1[3|4|5|6|7|8|9][0-9]{9}$)/;        if (reg.test(tel)) {            return true;        }else{            return false;        };    }        function checkEmail(email) {    	var reg = /^([a-zA-Z]|[0-9])(\w|\-)+@[a-zA-Z0-9]+\.([a-zA-Z]{2,4})$/;    	if (reg.test(email)) {            return true;        }else{            return false;        };    }</script></body></html>